  text-align: center; 
  font-size: 1.2vmin;
}


.serviceWorkspaceScenarioCount{
 text-align: center; 
 font-size: 1.2vmin;
}

.serviceWorkspaceIncCount{
 text-align: center; 
 font-size: 1.2vmin;
}

.table > thead > tr > th {
  border-bottom: 2px solid var(--card-text);
  font-weight: 500 !important;
  color: #333 !important;
 text-align: center !important; 
}
#workspaceAction {
   width: 100%;
   float: right;
   -webkit-transition: width 1s;
  transition: width 1s;
}
input:focus, .form-control:focus {
    border-color: transparent;
    outline: none;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(51, 59, 69, 0.6);
}
.messageContainer {
   height: 100%;
