   text-align: center;
   padding-top: 15%;
}
.message {
  font-size: 4vmin;
}
.workspaceCardContainer {
      display: block;
    margin: 0 auto;
    padding: 6px 10px;
    float: left;
    width: 100%;
    text-align: center;
}

@media only screen and (min-width: 1600px) {
  .workspaceCardContainer {
        width: 25%;
  }
}

@media only screen and (min-width: 1300px) and ( max-width: 1600px) {
  .workspaceCardContainer {
        width: 33%;
  }
}

@media only screen and (min-width: 1000px) and ( max-width: 1300px) {
  .workspaceCardContainer {
        width: 50%;
  }
}

