  .workspaceCardContainer {
        width: 80%;
        margin-left: 10%;
  }
}

.workspaceCard {
   width: 100%;
   height: 120px;  
   float: left;
   background: var(--card-background);
   color: var(--card-text);
}

.workspaceCard:hover{
  border: 2px solid var(--card-text);
}
.serviceWorkspaceCard > th{
  color: var(--card-text); 
text-align: center; 
font-weight: normal; 
font-size: 1.2vmin;
}

.workspaceCardRow {
    display: block;
    width: 101%;
    display: block;
    float: left;
    text-align: center;
   padding-left: 3px;
  padding-right: 3px;
   margin: 0 auto 4px auto;
}
.workspaceCardSubtitle {
 border-bottom: 2px solid var(--card-text);
margin: 8px auto;
}
.workspaceCardDataLabel {
    line-height:initial;
}

.cell { 
display: block;
    float: left;
    margin: auto;
    width: 33%;
}

.detailWorkspaceCard {
  width: 30%;
  height: 160px;  
  float: left;
  margin-left: 3%;
  margin-top: 1%;
}

.detailWorkSpaceCardTable {
  border-top: 3px solid var(--card-text);
  padding-top: 4px;
  overflow-y: scroll;
  height: 100px;
  text-align: left;
}

.listCellLabel {
  display: block;
  float: left;
  width: 40%;
  margin-left:10%;
}

.listCellValue {
  display: block;
  float:left;
  width: 50%;
    overflow-x: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}

.listRow {
  height: 30px;
  width: 100%;
  border-bottom: 1px solid var(--card-text);
  padding-top: 4px;
  display: inline;
}

.left-badge {
  float: left;
  font-weight: bold;
}

.inactive {
  color: green;
}

#impactedList {
    font-size: 1.3vmin !important;
}

.impactedListItem {
    display: block;
    width: 100%;
    float:left;
    display: block;
    text-align: center;
   margin: 4px auto;
   cursor: pointer;
}

.impactedListItem:hover {
    background-color: var(--card-background-alt);
    color: var(--card-text-alt);
}
.impactedListItem:hover .impactedNameContainer {
    color: var(--card-text-alt);
}

.impactedCountContainer{ 
display: block;
    float: left;
    margin: auto auto auto 6px;
    padding: 2%;
    min-width: 45px;
}
.impactedCount {
    padding: 3px 23px;
    border-radius: 10em;
    border: 2px solid var(--severity);
}

.impactedNameContainer{ 
    display: block;
    float: left;
    margin: auto;
    max-width: calc(100% - 5vw);
    padding: 2%; 
    overflow-x: hidden;
    text-overflow: ellipsis;
     white-space: nowrap;
    text-align: left;
}

.serviceWorkspace {
  overflow-x: hidden;
}

.workSpaceCardTitle {
font-size: 1.8vmin; 
font-weight: 550;
text-align: center;
padding: 3% 0% 5px 10.5%;
margin: auto; 
text-overflow: ellipsis; 
overflow: hidden;
white-space: nowrap;
}

.workspaceDataPoint{
 text-align: center; 
padding: 2.5%;
}

