    .widget-body {
        display: flex;
        justify-content: space-between;
        align-items: center;
        flex-wrap: wrap;
        padding: 1vh 1vw;
        height: Calc(100% - var(--widget-header-height,40px));
        width: 100%;
        margin-top:var(--widget-header-height,40px);
        overflow-y: auto;
    }
    .widget-header { 
        width: fit-content;
        text-align: center;
        padding: 0 3em;
        background: var(--widget-header-background,#333);
        color: var(--widget-header-text,#FFF);
        height: var(--widget-header-height,40px);
        font-size: var(--widget-header-font-size,26px);
